package com.smartkirana.aims.aimsshop.views.fragments.NavCategories;import com.google.gson.annotations.Expose;import com.google.gson.annotations.SerializedName;import java.util.List;public class CategoriesListModel {    @SerializedName("categories")    @Expose    private List<Category> categories = null;    public List<Category> getCategories() {        return categories;    }    public void setCategories(List<Category> categories) {        this.categories = categories;    }    public class Category {        @SerializedName("category_id")        @Expose        private String categoryId;        @SerializedName("name")        @Expose        private String name;        @SerializedName("children")        @Expose        public List<Child> children = null;        @SerializedName("column")        @Expose        private String column;        @SerializedName("path")        @Expose        private String path;        public String getCategoryId() {            return categoryId;        }        public void setCategoryId(String categoryId) {            this.categoryId = categoryId;        }        public String getName() {            return name;        }        public void setName(String name) {            this.name = name;        }        public List<Child> getChildren() {            return children;        }        public void setChildren(List<Child> children) {            this.children = children;        }        public String getColumn() {            return column;        }        public void setColumn(String column) {            this.column = column;        }        public String getPath() {            return path;        }        public void setPath(String path) {            this.path = path;        }    }    public class Child {        @SerializedName("name")        @Expose        private String name;        @SerializedName("path")        @Expose        private String path;        public String getName() {            return name;        }        public void setName(String name) {            this.name = name;        }        public String getPath() {            return path;        }        public void setPath(String path) {            this.path = path;        }    }}